import React, { Component } from 'react';
import { Router, Route } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import LoginPage from './components/loginPage'
import RegisterPage from './components/registerPage'
import {PrivateRoute} from './components/privateRoute'
import User from './components/user'

const history = createBrowserHistory();
console.log(history)
class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      authorized: false
  };
}
isAuthorized = ()=>{
  this.setState({
    authorized: true
  })
}
  render() {
    let state = this.state.authorized
    return (
      <Router history={history}>
          <div>
            <PrivateRoute exact path="/" component={User} authorized={this.state.authorized}/>
            <Route path="/login" render={()=><LoginPage authorized={state} isAuthorized={this.isAuthorized}/>}  />
            <Route path="/register" component={RegisterPage} authorized={this.state.authorized}/>
          </div>
      </Router>
    );
  }
}

export default App;
