import React, { Component } from 'react';
import { Router, Route } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import LoginPage from './components/loginPage'
import RegisterPage from './components/registerPage'
import {PrivateRoute} from './components/privateRoute'
import User from './components/user'

const ConnectyCube = window.ConnectyCube

const history = createBrowserHistory();

const CREDENTIALS = {
  appId: 629,
  authKey: 'gy9ARS4acJh9DHb',
  authSecret: 'YfQFkHu3h9WtdPw'
}

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      authorized: false,
      session: {}
  };
}
componentDidMount(){
  ConnectyCube.init(CREDENTIALS);
  if(localStorage.getItem('user')){
    let userCredentials = {login: localStorage['user'].login, password: localStorage['user'].password};
    let cont = this
    ConnectyCube.createSession(userCredentials, function(error, session) {
      if(error){
        cont.setState({
          authorized: false
        })
      }else{
        cont.setState({
          authorized: true,
          session: session
        })
      }
    });
  }
}
  isAuthorized= () =>{
    this.setState({
      authorized: true
    })
  }
  sessionSetupWithLogin = (newSession) =>{
    this.setState({
      session: newSession
    })
  }
  render() {
    return (
      <Router history={history}>
          <div>
            <PrivateRoute exact path="/" component={User} authorized={this.state.authorized} session={this.state.session}/>
            <Route path="/login" render={()=><LoginPage authorized={this.state.authorized} isAuthorized={this.isAuthorized} setSession={this.sessionSetupWithLogin}/>}  />
            <Route path="/register" render={()=><RegisterPage authorized={this.state.authorized}/>}/>
          </div>
      </Router>
    );
  }
}

export default App;
