import React from 'react';
import UserAvatar from './user-avatar'
import UserInfo from './user-info'

const ConnectyCube = window.ConnectyCube

class User extends React.Component{
    removeLocalStorage = ()=>{
        let thisProps = this.props
        ConnectyCube.destroySession(function(error) {
            if(!error){
                localStorage.removeItem("user")
                thisProps.resetSession()
                thisProps.setAuthorization()
            }
        });
        
    }
    render(){
        console.log(this.props)
        return(
            <div className="user-profile">
                <div className="user-avatar">
                    <UserAvatar />
                </div>
                <UserInfo session={this.props.session}/>
                <span className="logout" onClick={this.removeLocalStorage}>remove</span>
            </div>
            
        )
    }
}

export default User;
