import React from 'react';
import { Link , Redirect } from 'react-router-dom';

const ConnectyCube = window.ConnectyCube

class RegisterPage extends React.Component{
    constructor(props) {
        super(props);
    
        this.state = {
          registered: false,
          error: false
      };
    }
    registerNewUser = () =>{
        let user = {
            login : this.login.value,
            password: this.password.value,
            full_name: this.name.value
        }
        let cont = this
        ConnectyCube.users.signup(user, function(error, user){
            if(error){
                console.log(error)
              cont.setState({
                
                  error: true
              })
            }else{
                //cont.setLocalStorage()
            }
        })
    }
    render(){
        if(!localStorage.getItem('user')){
            return(
                <div>registration
                    <div className="form">
                        <div><input type="text" placeholder="Login" ref={(input)=>this.login = input}/></div>
                            
                        <div><input type="text" placeholder="Password" ref={(input)=>this.password = input}/></div>
                            
                        <div><input type="text" placeholder="Name" ref={(input)=>this.name = input}/></div>
                            
                        <div><button onClick={this.registerNewUser}>btn</button></div>
                            
                            {this.state.error && 
                                <div> incorrect data</div>
                            }
                            {this.state.registered && 
                                <div> Successful registration</div>
                            }
                    </div>
                    <Link to="/login" className="btn btn-link">login</Link>
                </div>
            )
        }else{
           return(
                <Redirect to={{ pathname: '/' }}/>
           )
        }
    }
}
export default RegisterPage;
