import React from 'react';
import { Link , Redirect } from 'react-router-dom';

const ConnectyCube = window.ConnectyCube

class LoginPage extends React.Component{
    constructor(props) {
        super(props);
    
        this.state = {
          error: false
      };
    }
    setLocalStorage = (user) =>{
        localStorage.setItem('user', true)
        localStorage['user'] = {login: user.login, password: user.password}
        this.props.setAuthorization()
    }
    loginUser = () =>{
        let user = {
            login : this.login.value,
            password: this.password.value,
        }
        let cont = this
        ConnectyCube.createSession(user, function(error, session) {
            if(error){
              console.log(error)
              cont.setState({
                error: true
              })
            }else{
              cont.setState({
                error: false
              })
              cont.props.setSession(session)
              cont.setLocalStorage(user)
            }
          });
    }
    removeLocalStorage = ()=>{
        localStorage.removeItem("user")
        
    }
    render(){
            if(!this.props.authorization){
                return(
                    <div>login
                        <div className="form">
                                <input type="text" className={this.state.error && "error"} placeholder="Login" ref={(input)=>this.login = input}/>
                                <input type="text" placeholder="Password" ref={(input)=>this.password = input}/>
                                <button onClick={this.loginUser}>btn</button>
                                {this.state.error && 
                                <div> incorrect login or email</div>
                            }
                        </div>
                        <Link to="/register" className="btn btn-link">Register</Link>
                    </div>
                )
            }else{
               return(
                    <Redirect to={{ pathname: '/' }}/>
               )
            }
    }
}
export default LoginPage;
