import React from 'react';
import { Link , Redirect } from 'react-router-dom';

const ConnectyCube = window.ConnectyCube

class LoginPage extends React.Component{
    
    setLocalStorage = () =>{
        localStorage.setItem('user', true)
        this.props.isAuthorized()
    }
    registerNewUser = () =>{
        let user = {
            login : this.login.value,
            password: this.password.value,
            full_name: this.name.value
        }
        let cont = this
        ConnectyCube.users.signup(user, function(error, user){
            if(error){
              //console.log(error)
            }else{
                cont.setLocalStorage()
            }
        })
    }
    removeLocalStorage = ()=>{
        localStorage.removeItem("user")
        
    }
    render(){
            if(!localStorage.getItem('user')){
                return(
                    <div>login
                        <div className="form">
                                <input type="text" placeholder="Login" ref={(input)=>this.login = input}/>
                                <input type="text" placeholder="Password" ref={(input)=>this.password = input}/>
                                <button onClick={this.registerNewUser}>btn</button>
                        </div>
                        <Link to="/register" className="btn btn-link">Register</Link>
                    </div>
                )
            }else{
               return(
                    <Redirect to={{ pathname: '/' }}/>
               )
            }
    }
}
export default LoginPage;
