import React, { Component } from 'react';
import { Router, Route } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import LoginPage from './components/loginPage'
import RegisterPage from './components/registerPage'
import {PrivateRoute} from './components/privateRoute'
import User from './components/user'

const ConnectyCube = window.ConnectyCube

const history = createBrowserHistory();

const CREDENTIALS = {
  appId: 629,
  authKey: 'gy9ARS4acJh9DHb',
  authSecret: 'YfQFkHu3h9WtdPw'
}

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      authorized: false
  };
}

  isAuthorized= () =>{
    this.setState({
      authorized: true
    })
  }
  render() {
    console.log(this.state.authorized)
    return (
      <Router history={history}>
          <div>
            <PrivateRoute exact path="/" component={User} authorized={this.state.authorized}/>
            <Route path="/login" render={()=><LoginPage authorized={this.state.authorized} isAuthorized={this.isAuthorized}/>}  />
            <Route path="/register" render={()=><RegisterPage authorized={this.state.authorized}/>}/>
          </div>
      </Router>
    );
  }
}

export default App;
