import React from 'react';


class LoginPage extends React.Component{
    constructor(props) {
        super(props);
    
        this.state = {
          authorized: false
      };
    }
    setLocalStorage = () =>{
        localStorage.setItem('user', true)
        this.setState({
            authorized: true
        })
    }
    removeLocalStorage = ()=>{
        localStorage.removeItem("user")
        this.setState({
            authorized: false
        })
    }
    render(){
        console.log(localStorage)
            return(
                <div>login
                    <button onClick={this.setLocalStorage}>set</button>
                    <button onClick={this.removeLocalStorage}>remove</button>
                </div>
            )
    }
}
export default LoginPage;
