import React from 'react';
import { Route, Redirect } from 'react-router-dom';

const ConnectyCube = window.ConnectyCube

class LoginPage extends React.Component{
    
    setLocalStorage = () =>{
        localStorage.setItem('user', true)
        this.props.isAuthorized()
    }
    registerNewUser = (e) =>{
        let user = {
            login : this.login.value,
            password: this.password.value,
            full_name: this.name.value
        }
        ConnectyCube.users.signup(user, function(error, user){
            console.log(e)
            if(error){
              //console.log(error)
            }else{
                console.log(this)
            }
        }).bind(this)
    }
    removeLocalStorage = ()=>{
        localStorage.removeItem("user")
        
    }
    render(){
        console.log(localStorage)
            if(!localStorage.getItem('user')){
                return(
                    <div>login
                        <button onClick={this.setLocalStorage}>set</button>
                        <button onClick={this.removeLocalStorage}>remove</button>
                        <div className="form">
                                <input type="text" placeholder="Login" ref={(input)=>this.login = input}/>
                                <input type="text" placeholder="Password" ref={(input)=>this.password = input}/>
                                <input type="text" placeholder="Name" ref={(input)=>this.name = input}/>/>
                                <button onClick={this.registerNewUser}>btn</button>
                        </div>
                    </div>
                )
            }else{
               return(
                    <Redirect to={{ pathname: '/' }}/>
               )
            }
    }
}
export default LoginPage;
